using namespace std;

class Solution {
public:
    vector<int> findSubstring(string S, vector<string> &L) {
        vector<int> res;
		unordered_map<string, int> stat;
		unordered_map<string, int> run;
        int len = S.size();
		int num = L.size();
		int per = 0;

		if (num == 0 || !(per = L[0].size())) return res;
		
		int part= num * per;
		if (part > len) return res;
		
		int end = len - part;
        unordered_map<string, int>::iterator iter;
        pair<unordered_map<string, int>::iterator, bool> ir;
        
        for (int i=0; i<num; i++) {
			ir = stat.insert(pair<string, int>(L[i], 1));
			if (ir.second == false){
				ir.first->second++;				
			}
		}
		
		int i, j, pos, wc;
		string pre;
		for (i=0; i<=end; i++) {
			pos = i;
			for (j=0; j<num; j++, pos += per) {
				string seg = S.substr(pos, per);
				if (j == 0 || seg != pre) {
					iter = stat.find(seg);
					if (iter == stat.end()) break;
					wc = iter->second;
					ir = run.insert(pair<string, int>(seg, 1));
					iter = ir.first;
					if (ir.second) {
						pre = seg;
						continue;
					}
				}
				iter->second++;
				if (wc < iter->second) break;
			}
			if (j == num) res.push_back(i);
			run.clear();
    	}
    	return res;
    }
};

class Solution2 {
public:
    vector<int> findSubstring(string S, vector<string> &L) {
        vector<int> result_set;
        vector<int> vi;
        vector<int> vic;
        unordered_map<string, int> um;
        unordered_map<string, int>::iterator uit;
        int wl;
        int nc;
        int n = (int)L.size();
        int slen = (int)S.length();
        
        if (slen == 0 || n == 0) {
            return result_set;
        }
        wl = (int)L[0].length();
        if (wl == 0) {
            return result_set;
        }
        if (slen < n * wl) {
            return result_set;
        }
        
        um.clear();
        vi.clear();
        nc = 0;
        
        int i, j;
        for (i = 0; i < n; ++i) {
            uit = um.find(L[i]);
            if (uit == um.end()) {
                um[L[i]] = nc;
                vi.push_back(1);
                ++nc;
            } else {
                ++vi[um[L[i]]];
            }
        }
        vic.resize(nc);
        
        int cc, ll, rr;
        int idx;
        string str;
        
        for (i = 0; i < wl; ++i) {
            for (j = 0; j < nc; ++j) {
                vic[j] = vi[j];
            }
            cc = 0;
            
            ll = i;
            rr = ll;
            while (rr + wl <= slen) {
                str = S.substr(rr, wl);
                uit = um.find(str);
                if (uit != um.end()) {
                    idx = uit->second;
                    if (vic[idx] == 0) {
                        while (true) {
                            str = S.substr(ll, wl);
                            if (um[str] != idx) {
                                ll += wl;
                                ++vic[um[str]];
                                --cc;
                            } else {
                                ll += wl;
                                ++vic[um[str]];
                                --cc;
                                break;
                            }
                        }
                    } else {
                        --vic[idx];
                        ++cc;
                        rr += wl;
                    }
                } else {
                    ll = rr + wl;
                    for (j = 0; j < nc; ++j) {
                        vic[j] = vi[j];
                    }
                    rr = ll;
                    cc = 0;
                }
                if (cc == n) {
                    result_set.push_back(ll);
                    str = S.substr(ll, wl);
                    idx = um[str];
                    ++vic[idx];
                    --cc;
                    ll += wl;
                }
            }
        }
        vic.clear();
        
        return result_set;
    }
};

class Solution3 {
public:
    vector<int> findSubstring(string S, vector<string> &L) {
        vector<int> res;
        unordered_map<string, int> stat;
		unordered_map<string, int> run;
		int len = S.size();
		int num = L.size();
		int per = 0;

		if (num == 0 || !(per = L[0].size())) return res;
		
		int part = num * per;
		if (part > len) return res;
		
		unordered_map<string, int>::iterator iter;
        pair<unordered_map<string, int>::iterator, bool> ir;
		
		for (int i = 0; i < num; i++) {
			ir = stat.insert(pair<string, int>(L[i], 0));
			ir.first->second++;
		}
		int wc;
		for (int i = 0; i < per; i++) {
			int step = 0;
			run.clear();
			// scan like a worm, string[spos, epos] is the candidate
			int spos=i, epos = i + per - 1;
			for (; epos < len; epos += per) {
				string seg = S.substr(epos - per + 1, per);
				iter = stat.find(seg);
				// encounter some word not in L
				if (iter == stat.end()) {
					spos = epos + 1;
					step = 0;
					run.clear();
					continue;
				}
				
				wc = iter->second;
				step++;
				
				ir = run.insert(pair<string, int>(seg, 0));
				iter = ir.first;
				iter->second++;
				
				// string[spos, epos] is matched
				if (iter->second == wc && step == num) { 
					res.push_back(spos);
					run.find(S.substr(spos, per))->second--;
					step--;
					spos += per;
					continue;
				}
				
				// number of duplicated word exceeds needed
				if (iter->second > wc) {			
					string tmp = S.substr(spos, per);
					// find the first duplicated one
					while(seg != tmp) {
						run.find(tmp)->second--;
						step--;
						spos += per;
						tmp = S.substr(spos, per);
					}
					// then skip it
					iter->second--;
					spos += per;
					step--;
				}
			}
		}
    	return res;
    } 
};

